    {

      var ASCIICode = (evt.which) ? evt.which : evt.keyCode
        if (ASCIICode > 31 && (ASCIICode < 65 || ASCIICode > 90) && (ASCIICode < 97 || ASCIICode > 122) && ASCIICode != 32 )
            return false;
        return true;


    }
	function onlyNumberKey(evt) {

